
    var lockmethod  =  require('..');

		var time = 3000;	

		var obj = { changeState  :  lockmethod(
			function(state,cb){
				setTimeout(function(){
					cb(state+time)		
				},time)	
				time -= 300;
			}
		)
		}

		obj.changeState('a',function(r,next){console.log(r);
				setTimeout(function(){
					console.log('i love you!!');	
					next();
					},3000)
				});
		obj.changeState('b',function(r,next){console.log(r);next();});
		obj.changeState('c',function(r,next){console.log(r);next();});

